'use strict'
import React, { Component } from 'react'


const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition
const recognition = new SpeechRecognition()

recognition.continous = true
recognition.interimResults = true
recognition.lang = 'en-US'


class VoiceRecognition extends Component {
	constructor() {
    super()
    	this.state = {
     	 listening: false
    }
	    this.toggleListen = this.toggleListen.bind(this)
	    this.handleListen = this.handleListen.bind(this)
  	}

  toggleListen() {
    this.setState({
      listening: !this.state.listening
    }, this.handleListen)
  }

	handleListen = () => {
		if(this.state.listening) recognition.start()

		let finalTranscript =''
		recognition.onresult = event => {
			let interimTranscript =''
			console.log(event.result)
			for (let i = event.resultIndex; i < event.result.length; i++)
			{
				const transcript = event.results[i][0].transcript
				if(event.result[i].isFinal) finalTranscript +=transcript + "";
				else interimTranscript +=transcript;
			}

			document.getElementById('interim').innerHTML = interimTranscript
			document.getElementById('final').innerHTML = finalTranscript
		}
	}


	render() {
		return (
			<div style={container}>
				<button id = 'microphone-btn' style ={button} onClick={this.toggleListen} />
				<div id = 'interim' style={final}></div>
				 <div id='final' style={final}></div>
     		</div>
		)
	}
}


export default VoiceRecognition;

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    textAlign: 'center'
  },
  button: {
    width: '60px',
    height: '60px',
    background: 'lightblue',
    borderRadius: '50%',
    margin: '6em 0 2em 0'
  },
  interim: {
    color: 'gray',
    border: '#ccc 1px solid',
    padding: '1em',
    margin: '1em',
    width: '300px'
  },
  final: {
    color: 'black',
    border: '#ccc 1px solid',
    padding: '1em',
    margin: '1em',
    width: '300px'
  }
}

const { container, button, interim, final } = styles
