import React from 'react'
import Photo from '../Photo/Photo'
import Tasks from '../Tasks/Tasks'


class Profile extends React.Component  {

	constructor(props) {
		super(props);
		this.state={
			nameInput:'',
			emailInput: '',
			ageInput: '',
			showEditUser: false
		}
	}

	// onNameChange = (event) => {
	// 	this.setState({nameInput: event.target.value})
	// }
	// onEmailChange = (event) => {
	// 	this.setState({emailInput: event.target.value})
	// }
	// onAgeChange = (event) => {
	// 	this.setState({ageInput: event.target.value})
	// }

	deleteUser = () => {
		const { onRouteChange, user } = this.props
		fetch('https://blech-task-manager.herokuapp.com/users/me/', {
			method: 'delete',
			headers: {
				'content-type': 'application/json',
				Authorization: `Bearer ${user.token}`
			}

		})
		.then(response => response.json())
		.then(jsonResponse => {
			console.log(jsonResponse)
			if(jsonResponse._id) {
				onRouteChange('signout')
			}
		})	
	}

	showUserDetails = () => {
		const { user } = this.props
		return(
			<article 
			className = 'grow pointer mw5 center br3 pa4-ns mv3 b--black-10'
			onClick={()=> this.setState({showEditUser: true})}
			>
				<div className = 'tc'>
					<h1 className = 'f3 mb2'>{`${user.name}`}</h1>
					<h2 className = 'f5 fw4 black mt0'>{`email: ${user.email}`}</h2>
					<h2 className = 'f5 fw4 black mt0'>{`age: ${user.age}`}</h2>
				</div>
			</article>
		)
	}

	onSubmitUpdate = () => {
		const { user } = this.props
		const {nameInput, emailInput, ageInput} = this.state
		fetch(`https://blech-task-manager.herokuapp.com/users/me`, {
			method: 'PATCH',
			headers: {
				'content-type': 'application/json; charset=UTF-8',
				Authorization: `Bearer ${user.token}`
			},

			body: JSON.stringify({
				name: nameInput,
				email: emailInput,
				age: ageInput
			})
		})
		.then(response=> response.json())
		.then(jsonData=> {
			if (jsonData._id) {
				console.log(jsonData)
				// this.props.loadUser(jsonData, user.token)
			}
		})
	}

	showUserEditForm = () => {
		const {user} = this.props
		return (
			<div className="pa4 black-80 flex justify-center">
			  <div className="measure">
			    <label form="userDetails" className="f6 b db mb2">Name</label>
			    <input 
			    id="name" 
			    className="input-reset ba b--black-20 pa2 mb2 db w-100" 
			    type="text" 
			    onChange={() => this.onNameChange()}
			    value = {user.name}
			    />
			    <input 
			    id="email" 
			    className="input-reset ba b--black-20 pa2 mb2 db w-100" 
			    type="text" 
			    onChange={() => this.onEmailChange()}
			    value = {user.email}
			    />
			    <input 
			    id="age" 
			    className="input-reset ba b--black-20 pa2 mb2 db w-100" 
			    type="text" 
			    onChange={() => this.onAgeChange()}
			    value = {user.age}
			    />
			    <button
			    className='ph3 link dim f6 ph3 pv2 mb2 dib white bg-navy'
			    type='update'
			    onClick={() => this.onSubmitUpdate()}
			    >Update</button>
			  </div>
			</div>
		);
	}

	render() {
		const {user} = this.props
		return (
		<div>
			{
				(this.state.showEditUser)
				?
				this.showUserEditForm()
				:
				this.showUserDetails()
			}
			<Photo user={user}/>
			<br />
			<Tasks user={user}/>
			<div>
				<button className='w-30 grow f4 link ph3 pv dib white bg-light-purple'
				onClick= {this.deleteUser}
				>Delete User</button>
			</div>


		</div>
		);
	}


}

export default Profile;
