import React from 'react'
import anonymous from './anonymous.png'




class Photo extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
			avatar: false
		}

	}


	getUserAvatar = async() => {
		const user = this.props.user
		console.log('user token', user.token)
		try {
			await fetch(`https://blech-task-manager.herokuapp.com/users/${user._id}/avatar`, {
			method: 'get',
			headers: {
				Authorization: `Bearer ${user.token}`
			}
			})
			.then(response => {
				if(response.status === 200) {
					this.setState({avatar: true})
				}
			})
		} catch(err) {
			this.setState({avatar: false})
		}
	}

	componentDidMount() {
		this.getUserAvatar()
	}


	onPhotoChangeClick = () => {
		// console.log(document.querySelector('#myfile').files[0])
		const fileInput = document.querySelector('#myfile').files[0]
		const formData = new FormData()
		formData.append('avatar', fileInput)
		fetch('https://blech-task-manager.herokuapp.com/users/me/avatar', {
			method: 'post',
			body: formData,
			headers: {
				Authorization: `Bearer ${this.props.user.token}`
			}
		})
		.then(response => {
			if(response.status === 200) {
			this.setState({avatar: true})
			}
		})
	}

	onPhotoDeleteClick = () => {
		fetch('https://blech-task-manager.herokuapp.com/users/me/avatar', {
			method: 'delete',
			headers: {
				Authorization: `Bearer ${this.props.user.token}`
			}
		})
		this.setState({avatar: false})

	}

	render() {
		const { user } = this.props
		return (
			<div >
				<div className='center pointer '>
				{
					(this.state.avatar) 
					? 
					<img alt='' 
					src = {`https://blech-task-manager.herokuapp.com/users/${user._id}/avatar`} 
					width='300px' height='auto'/>
					:
					<img alt='' 
					src = {anonymous} 
					width='300px' height='auto'/>
				}
				</div>
				<a class="child absolute top-1 right-1 ba bw1 black-40 grow no-underline br-100 w1 h1 pa2 lh-solid b" href="#">Ã—</a>
				<br/>
				<div className='tc bg-blue dib br3 pa1 ma4 grow bw2 w-30 shadow-5'>
						<div>
						<h4>
							<form className='f5 white ma4' action="/action_page.php">
						  	<input type="file" id="myfile" name="myfile" />
							</form>
						</h4>
						</div>

						<div>
						<p>
							<button
							className='outline w-15 f6 link dim ph3 pv2 mb2 dib white bg-dark-blue'
							type='submit'
							onClick={this.onPhotoChangeClick}
							>Update Photo</button>
							<button 
							className='outline w-15 f6 link dim ph3 pv2 mb2 dib white bg-dark-pink'
							type='delete'
							onClick={this.onPhotoDeleteClick}
							>Delete Photo</button>
						</p>
						</div>
				</div>

			</div>
		);
	}

}

export default Photo
