import React from 'react'

class EditTaskForm extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			user: this.props.user,
			description: this.props.description,
			completed: this.props.completed,
			taskToEditId: this.props.taskToEditId
		}
		console.log('task id is: ', this.props.taskToEditId)
	}

	onDescriptionChange = (event) => {
		this.setState({description: event.target.value})
	}


	onSubmitEdit = () => {
		const { user, description, completed, taskToEditId } = this.state
		fetch(`https://blech-task-manager.herokuapp.com/tasks/${_id}`, {
			method: 'patch',
			headers: {
				'content-type': 'application/json',
				Authorization: `Bearer ${user.token}`
			},

			body: JSON.stringify({
				description: description,
				completed: completed
			})
		})
		.then(response=> response.json())
		.then(jsonData=> {
			console.log('add task: ', jsonData)
			if (jsonData.description) {
				const { user, token } = jsonData
				this.props.getUserTasks()
				this.props.changeAddTaskButtonState(true)

			}
		})
	}


	render() {
		return (
			<div className="pa4 black-80 flex justify-center">
			  <div className="measure ">
			    <label form="description" className="f6 b db mb2">Task Description</label>
			    <input 
			    id="description" 
			    className="input-reset ba b--black-20 pa2 mb2 db w-100" 
			    type="text" 
			    aria-describedby="description-desc"
			    placeholder = {this.state.description}
			    onChange={this.onDescriptionChange}
			    />
			    <button
			    className='ph3 link dim f6 ph3 pv2 mb2 dib white bg-navy'
			    type='submit'
			    onClick={this.onSubmitEdit}
			    >Update</button>
			  </div>
			</div>
		);
	} 

}

export default EditTaskForm;
