import React from 'react'

class AddTaskForm extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			description: '',
			completed: false
		}
	}

	onDescriptionChange = (event) => {
		this.setState({description: event.target.value})
	}


	onSubmitAdd = () => {
		fetch('https://blech-task-manager.herokuapp.com/tasks', {
			method: 'post',
			headers: {'content-type': 'application/json'},
			body: JSON.stringify({
				description:this.state.description,
				completed: this.state.completed
			})
		})
		.then(response=> response.json())
		.then(jsonData=> {
			console.log('add task: ', jsonData)
			if (jsonData.task) {
				const { user, token } = jsonData
				this.props.changeAddTaskButtonState(true)
			}
		})
	}


	render() {
		return (

			<form class="pa4 black-80 flex justify-center">
			  <div class="measure ">
			    <label for="description" class="f6 b db mb2">Task Description</label>
			    <input 
			    id="description" 
			    class="input-reset ba b--black-20 pa2 mb2 db w-100" 
			    type="text" 
			    aria-describedby="description-desc"
			    onChange={this.onDescriptionChange}
			    />
			    <button
			    className='ph3 link dim f6 ph3 pv2 mb2 dib white bg-navy'
			    type='submit'
			    onClick={this.onSubmitAdd}
			    >Add</button>
			  </div>
			</form>
		);
	} 

}

export default AddTaskForm;
