import React from 'react'

const Task = ({ description, completed }) => {

	return (
		<div className='tc bg-light-green dib br3 pa3 ma2 grow bw2 shadow-5'>
			<div>
				<h2>{description}</h2>
				{
					(completed) 
					?
					<p>completed</p>
					:
					<p>{completed}</p>
				}
			</div>
			<div className = 'flex hustify-between'>
				<p>Edit</p>
				<p>Completed</p>
			</div>
		</div>
	);
}

export default Task;
